const Container = ({ children, className = '' }) => (
  <div className={`max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 ${className}`}>
    {children}
  </div>
);

const Navbar = ({ logo, links }) => (
  <nav className="bg-white border-b border-gray-200">
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
      <div className="flex justify-between h-16">
        <div className="flex">
          <div className="flex-shrink-0 flex items-center font-bold text-xl text-indigo-600">
            {logo}
          </div>
          <div className="hidden sm:ml-6 sm:flex sm:space-x-8">
            {links.map((link) => (
              <a key={link.name} href={link.href} className="border-transparent text-gray-500 hover:border-gray-300 hover:text-gray-700 inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium">
                {link.name}
              </a>
            ))}
          </div>
        </div>
      </div>
    </div>
  </nav>
);

const TwoColumnLayout = ({ sidebar, children }) => (
  <div className="flex flex-col md:flex-row min-h-screen">
    <aside className="w-full md:w-1/4 bg-gray-50 border-r border-gray-200 p-6">
      {sidebar}
    </aside>
    <main className="w-full md:w-3/4 p-6 bg-white">
      {children}
    </main>
  </div>
);

const PrimaryButton = ({ children, onClick, type = 'button', disabled = false }) => (
  <button
    type={type}
    onClick={onClick}
    disabled={disabled}
    className={`inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 ${disabled ? 'opacity-50 cursor-not-allowed' : ''}`}
  >
    {children}
  </button>
);

const SecondaryButton = ({ children, onClick, type = 'button' }) => (
  <button
    type={type}
    onClick={onClick}
    className="inline-flex items-center px-4 py-2 border border-gray-300 shadow-sm text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
  >
    {children}
  </button>
);

const Input = ({ label, id, type = 'text', placeholder, value, onChange }) => (
  <div className="mb-4">
    <label htmlFor={id} className="block text-sm font-medium text-gray-700 mb-1">
      {label}
    </label>
    <input
      type={type}
      name={id}
      id={id}
      className="shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
      placeholder={placeholder}
      value={value}
      onChange={onChange}
    />
  </div>
);

const Select = ({ label, id, options, value, onChange }) => (
  <div className="mb-4">
    <label htmlFor={id} className="block text-sm font-medium text-gray-700 mb-1">
      {label}
    </label>
    <select
      id={id}
      name={id}
      className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md border"
      value={value}
      onChange={onChange}
    >
      {options.map((opt) => (
        <option key={opt.value} value={opt.value}>{opt.label}</option>
      ))}
    </select>
  </div>
);

const Card = ({ title, children, className = '' }) => (
  <div className={`bg-white overflow-hidden shadow rounded-lg ${className}`}>
    {title && (
      <div className="px-4 py-5 sm:px-6 border-b border-gray-200">
        <h3 className="text-lg leading-6 font-medium text-gray-900">{title}</h3>
      </div>
    )}
    <div className="px-4 py-5 sm:p-6">
      {children}
    </div>
  </div>
);

const GridSection = ({ items, renderItem }) => (
  <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
    {items.map((item, index) => (
      <div key={index} className="col-span-1">
        {renderItem(item)}
      </div>
    ))}
  </div>
);

const Alert = ({ type = 'info', message }) => {
  const styles = {
    success: 'bg-green-50 text-green-800',
    error: 'bg-red-50 text-red-800',
    warning: 'bg-yellow-50 text-yellow-800',
    info: 'bg-blue-50 text-blue-800',
  };
  return (
    <div className={`rounded-md p-4 mb-4 ${styles[type]}`}>
      <div className="flex">
        <div className="ml-3">
          <p className="text-sm font-medium">{message}</p>
        </div>
      </div>
    </div>
  );
};

const Badge = ({ text, color = 'gray' }) => {
  const colors = {
    gray: 'bg-gray-100 text-gray-800',
    green: 'bg-green-100 text-green-800',
    red: 'bg-red-100 text-red-800',
    blue: 'bg-blue-100 text-blue-800',
  };
  return (
    <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${colors[color] || colors.gray}`}>
      {text}
    </span>
  );
};

const Modal = ({ isOpen, onClose, title, children }) => {
  if (!isOpen) return null;
  return (
    <div className="fixed z-10 inset-0 overflow-y-auto" aria-labelledby="modal-title" role="dialog" aria-modal="true">
      <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" aria-hidden="true" onClick={onClose}></div>
        <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
        <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
          <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
            <h3 className="text-lg leading-6 font-medium text-gray-900" id="modal-title">{title}</h3>
            <div className="mt-2">{children}</div>
          </div>
          <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
            <button type="button" onClick={onClose} className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm">Close</button>
          </div>
        </div>
      </div>
    </div>
  );
};

const DataTable = ({ columns, data }) => (
  <div className="flex flex-col">
    <div className="-my-2 overflow-x-auto sm:-mx-6 lg:-mx-8">
      <div className="py-2 align-middle inline-block min-w-full sm:px-6 lg:px-8">
        <div className="shadow overflow-hidden border-b border-gray-200 sm:rounded-lg">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                {columns.map((col) => (
                  <th key={col.key} scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    {col.title}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {data.map((row, idx) => (
                <tr key={idx} className={idx % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                  {columns.map((col) => (
                    <td key={col.key} className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {row[col.key]}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>
);

const Breadcrumb = ({ items }) => (
  <nav className="flex" aria-label="Breadcrumb">
    <ol className="flex items-center space-x-4">
      {items.map((item, index) => (
        <li key={index}>
          <div className="flex items-center">
            {index > 0 && (
              <svg className="flex-shrink-0 h-5 w-5 text-gray-300" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 20" aria-hidden="true">
                <path d="M5.555 17.776l8-16 .894.448-8 16-.894-.448z" />
              </svg>
            )}
            <a href={item.href} className={`text-sm font-medium ${index > 0 ? 'ml-4' : ''} ${index === items.length - 1 ? 'text-gray-500 pointer-events-none' : 'text-gray-500 hover:text-gray-700'}`}>
              {item.name}
            </a>
          </div>
        </li>
      ))}
    </ol>
  </nav>
);

const Spinner = () => (
  <div className="flex justify-center items-center">
    <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-indigo-500" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
    </svg>
  </div>
);

const Checkbox = ({ id, label, checked, onChange }) => (
  <div className="flex items-start">
    <div className="flex items-center h-5">
      <input
        id={id}
        name={id}
        type="checkbox"
        checked={checked}
        onChange={onChange}
        className="focus:ring-indigo-500 h-4 w-4 text-indigo-600 border-gray-300 rounded"
      />
    </div>
    <div className="ml-3 text-sm">
      <label htmlFor={id} className="font-medium text-gray-700">{label}</label>
    </div>
  </div>
);

const Avatar = ({ src, alt, size = 'md' }) => {
  const sizes = {
    sm: 'h-8 w-8',
    md: 'h-12 w-12',
    lg: 'h-16 w-16',
  };
  return (
    <img
      className={`inline-block rounded-full object-cover ${sizes[size]}`}
      src={src || 'https://via.placeholder.com/150'}
      alt={alt}
    />
  );
};

const TextArea = ({ label, id, placeholder, rows = 4, value, onChange }) => (
  <div className="mb-4">
    <label htmlFor={id} className="block text-sm font-medium text-gray-700 mb-1">
      {label}
    </label>
    <textarea
      id={id}
      name={id}
      rows={rows}
      className="shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
      placeholder={placeholder}
      value={value}
      onChange={onChange}
    />
  </div>
);

const ToggleSwitch = ({ label, checked, onChange }) => (
  <div className="flex items-center justify-between py-3">
    <span className="flex-grow flex flex-col">
      <span className="text-sm font-medium text-gray-900">{label}</span>
    </span>
    <button
      type="button"
      onClick={() => onChange(!checked)}
      className={`${checked ? 'bg-indigo-600' : 'bg-gray-200'} relative inline-flex flex-shrink-0 h-6 w-11 border-2 border-transparent rounded-full cursor-pointer transition-colors ease-in-out duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500`}
    >
      <span className={`${checked ? 'translate-x-5' : 'translate-x-0'} pointer-events-none inline-block h-5 w-5 rounded-full bg-white shadow transform ring-0 transition ease-in-out duration-200`} />
    </button>
  </div>
);

const RadioGroup = ({ label, options, value, onChange }) => (
  <div className="mb-4">
    <label className="text-base font-medium text-gray-900">{label}</label>
    <div className="mt-2 space-y-2">
      {options.map((opt) => (
        <div key={opt.value} className="flex items-center">
          <input
            id={opt.value}
            name="radio-group"
            type="radio"
            checked={value === opt.value}
            onChange={() => onChange(opt.value)}
            className="focus:ring-indigo-500 h-4 w-4 text-indigo-600 border-gray-300"
          />
          <label htmlFor={opt.value} className="ml-3 block text-sm font-medium text-gray-700">
            {opt.label}
          </label>
        </div>
      ))}
    </div>
  </div>
);

const RangeSlider = ({ label, min = 0, max = 100, value, onChange }) => (
  <div className="mb-4">
    <label className="block text-sm font-medium text-gray-700 mb-2">
      {label}: {value}
    </label>
    <input
      type="range"
      min={min}
      max={max}
      value={value}
      onChange={onChange}
      className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
    />
  </div>
);

const SearchBar = ({ placeholder = 'Search...', onSearch }) => (
  <div className="relative rounded-md shadow-sm">
    <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
      <svg className="h-5 w-5 text-gray-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
        <path fillRule="evenodd" d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" clipRule="evenodd" />
      </svg>
    </div>
    <input
      type="text"
      className="focus:ring-indigo-500 focus:border-indigo-500 block w-full pl-10 sm:text-sm border-gray-300 rounded-md p-2 border"
      placeholder={placeholder}
      onChange={onSearch}
    />
  </div>
);

const PasswordInput = ({ label, id, value, onChange }) => (
  <div className="mb-4">
    <label htmlFor={id} className="block text-sm font-medium text-gray-700 mb-1">{label}</label>
    <input
      type="password"
      id={id}
      name={id}
      className="shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
      value={value}
      onChange={onChange}
    />
  </div>
);

const Tabs = ({ tabs, activeTab, onTabClick }) => (
  <div className="border-b border-gray-200">
    <nav className="-mb-px flex space-x-8" aria-label="Tabs">
      {tabs.map((tab) => (
        <button
          key={tab.name}
          onClick={() => onTabClick(tab.name)}
          className={`
            ${activeTab === tab.name
              ? 'border-indigo-500 text-indigo-600'
              : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}
            whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm`}
        >
          {tab.name}
        </button>
      ))}
    </nav>
  </div>
);

const Stepper = ({ steps, currentStep }) => (
  <nav aria-label="Progress">
    <ol className="flex items-center">
      {steps.map((step, index) => (
        <li key={step.name} className={`${index !== steps.length - 1 ? 'relative pr-8 sm:pr-20' : ''}`}>
          <div className="flex items-center">
            <div className={`${index <= currentStep ? 'bg-indigo-600' : 'bg-gray-200'} h-8 w-8 rounded-full flex items-center justify-center text-white font-bold text-sm`}>
              {index + 1}
            </div>
            <span className="ml-2 text-sm font-medium text-gray-900">{step.name}</span>
          </div>
          {index !== steps.length - 1 && (
            <div className="absolute top-0 right-0 hidden h-full w-5 md:block" aria-hidden="true">
              <div className="h-0.5 w-full bg-gray-200 absolute top-4"></div>
            </div>
          )}
        </li>
      ))}
    </ol>
  </nav>
);

const VerticalMenu = ({ items }) => (
  <nav className="space-y-1">
    {items.map((item) => (
      <a
        key={item.name}
        href={item.href}
        className={`${item.current ? 'bg-gray-100 text-gray-900' : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'} flex items-center px-3 py-2 text-sm font-medium rounded-md`}
      >
        <span className="truncate">{item.name}</span>
      </a>
    ))}
  </nav>
);

const Accordion = ({ title, children, isOpen, onToggle }) => (
  <div className="border-b border-gray-200">
    <button
      className="flex justify-between items-center w-full py-4 text-left text-gray-800 hover:text-indigo-600 focus:outline-none"
      onClick={onToggle}
    >
      <span className="font-medium">{title}</span>
      <span className={`transform transition-transform ${isOpen ? 'rotate-180' : ''}`}>
        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" /></svg>
      </span>
    </button>
    {isOpen && (
      <div className="pb-4 text-gray-600">
        {children}
      </div>
    )}
  </div>
);

const Statistic = ({ label, value, trend }) => (
  <div className="px-4 py-5 bg-white shadow rounded-lg overflow-hidden sm:p-6">
    <dt className="text-sm font-medium text-gray-500 truncate">{label}</dt>
    <dd className="mt-1 text-3xl font-semibold text-gray-900">{value}</dd>
    {trend && (
      <div className={`mt-2 flex items-baseline text-sm font-semibold ${trend > 0 ? 'text-green-600' : 'text-red-600'}`}>
        {trend > 0 ? '↑' : '↓'} {Math.abs(trend)}%
      </div>
    )}
  </div>
);

const EmptyState = ({ title, description, action }) => (
  <div className="text-center py-12 border-2 border-gray-300 border-dashed rounded-lg">
    <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M20 13V6a2 2 0 00-2-2H6a2 2 0 00-2 2v7m16 0v5a2 2 0 01-2 2H6a2 2 0 01-2-2v-5m16 0h-2.586a1 1 0 00-.707.293l-2.414 2.414a1 1 0 01-.707.293h-3.172a1 1 0 01-.707-.293l-2.414-2.414A1 1 0 006.586 13H4" />
    </svg>
    <h3 className="mt-2 text-sm font-medium text-gray-900">{title}</h3>
    <p className="mt-1 text-sm text-gray-500">{description}</p>
    {action && <div className="mt-6">{action}</div>}
  </div>
);

const ProgressBar = ({ progress }) => (
  <div className="w-full bg-gray-200 rounded-full h-2.5">
    <div
      className="bg-indigo-600 h-2.5 rounded-full transition-all duration-300"
      style={{ width: `${progress}%` }}
    ></div>
  </div>
);

const Skeleton = ({ className = '' }) => (
  <div className={`animate-pulse bg-gray-200 rounded ${className}`}></div>
);

const Tooltip = ({ text, children }) => (
  <div className="relative group inline-block">
    {children}
    <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 hidden group-hover:block bg-gray-900 text-white text-xs rounded py-1 px-2 whitespace-nowrap z-10">
      {text}
      <div className="absolute top-full left-1/2 -ml-1 border-4 border-transparent border-t-gray-900"></div>
    </div>
  </div>
);

const Divider = ({ text }) => (
  <div className="relative py-4">
    <div className="absolute inset-0 flex items-center" aria-hidden="true">
      <div className="w-full border-t border-gray-300"></div>
    </div>
    {text && (
      <div className="relative flex justify-center">
        <span className="px-2 bg-white text-sm text-gray-500">{text}</span>
      </div>
    )}
  </div>
);

const PageHeader = ({ title, actions }) => (
  <div className="md:flex md:items-center md:justify-between mb-6">
    <div className="flex-1 min-w-0">
      <h2 className="text-2xl font-bold leading-7 text-gray-900 sm:text-3xl sm:truncate">{title}</h2>
    </div>
    <div className="mt-4 flex md:mt-0 md:ml-4">
      {actions}
    </div>
  </div>
);

const SectionTitle = ({ title, subtitle }) => (
  <div className="pb-5 border-b border-gray-200 mb-5">
    <h3 className="text-lg leading-6 font-medium text-gray-900">{title}</h3>
    {subtitle && <p className="mt-2 max-w-4xl text-sm text-gray-500">{subtitle}</p>}
  </div>
);

const Blockquote = ({ children, author }) => (
  <figure className="mx-0 my-4">
    <blockquote className="border-l-4 border-indigo-500 pl-4 italic text-gray-700">
      {children}
    </blockquote>
    {author && <figcaption className="mt-2 text-sm text-gray-500">— {author}</figcaption>}
  </figure>
);

const CodeBlock = ({ code }) => (
  <pre className="bg-gray-800 text-gray-100 p-4 rounded-md overflow-x-auto text-sm font-mono">
    <code>{code}</code>
  </pre>
);

const Kbd = ({ k }) => (
  <kbd className="inline-flex items-center border border-gray-200 rounded px-2 text-sm font-sans font-medium text-gray-400 bg-gray-100">
    {k}
  </kbd>
);

const LoginLayout = ({ logo, children }) => (
  <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
    <div className="sm:mx-auto sm:w-full sm:max-w-md">
      <div className="flex justify-center">{logo}</div>
    </div>
    <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
      <div className="bg-white py-8 px-4 shadow sm:rounded-lg sm:px-10">
        {children}
      </div>
    </div>
  </div>
);

const ImageGallery = ({ images }) => (
  <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
    {images.map((src, idx) => (
      <div key={idx} className="relative aspect-w-1 aspect-h-1">
        <img className="object-cover rounded-lg shadow-sm w-full h-full" src={src} alt={`Gallery ${idx}`} />
      </div>
    ))}
  </div>
);

const AspectRatio = ({ ratio = '16/9', children }) => (
  <div className="relative w-full" style={{ paddingBottom: ratio === '16/9' ? '56.25%' : '75%' }}>
    <div className="absolute inset-0 w-full h-full">
      {children}
    </div>
  </div>
);

const NotificationBadge = ({ count, children }) => (
  <div className="relative inline-block">
    {children}
    {count > 0 && (
      <span className="absolute top-0 right-0 -mt-1 -mr-1 px-2 py-0.5 rounded-full text-xs font-medium bg-red-500 text-white">
        {count}
      </span>
    )}
  </div>
);

const Chip = ({ label, onRemove }) => (
  <span className="inline-flex items-center py-0.5 pl-2.5 pr-1 rounded-full text-sm font-medium bg-indigo-100 text-indigo-700">
    {label}
    <button
      type="button"
      onClick={onRemove}
      className="flex-shrink-0 ml-0.5 h-4 w-4 rounded-full inline-flex items-center justify-center text-indigo-400 hover:bg-indigo-200 hover:text-indigo-500 focus:outline-none"
    >
      <span className="sr-only">Remove {label}</span>
      <svg className="h-2 w-2" stroke="currentColor" fill="none" viewBox="0 0 8 8">
        <path strokeLinecap="round" strokeWidth="1.5" d="M1 1l6 6m0-6L1 7" />
      </svg>
    </button>
  </span>
);

const DropdownMenu = ({ label, items, isOpen, onToggle }) => (
  <div className="relative inline-block text-left">
    <div>
      <button type="button" onClick={onToggle} className="inline-flex justify-center w-full rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none">
        {label}
        <svg className="-mr-1 ml-2 h-5 w-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
          <path fillRule="evenodd" d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" clipRule="evenodd" />
        </svg>
      </button>
    </div>
    {isOpen && (
      <div className="origin-top-right absolute right-0 mt-2 w-56 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5">
        <div className="py-1" role="menu" aria-orientation="vertical">
          {items.map((item) => (
            <a key={item.name} href={item.href} className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100" role="menuitem">
              {item.name}
            </a>
          ))}
        </div>
      </div>
    )}
  </div>
);

